import React, { useState } from 'react'
import { Link } from 'react-router-dom'

export default function SignUp() {
    const [ credentials, setcredentials ] = useState( { name: "", email: "", password: "", geolocation: "" } )

    const handleSubmit = async ( e ) => {
        e.preventDefault();
        const response = await fetch( "http://localhost:5000/api/createuser", {
            method: 'POST',
            header: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify( {
                name: credentials.name,
                email: credentials.email,
                password: credentials.password,
                location: credentials.geolocation
            } )
            
        } )
    }

    const onChange = async ( event ) => {
        setcredentials( { ...credentials, [ event.target.name ]: event.target.value } )
    }

    return (
        <>
            <div className='container'>
                <form onSubmit={ handleSubmit }>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Name</label>
                        <input type="email" className="form-control" name='name' value={ credentials.name } onChange={ onChange } placeholder="Enter Name" />
                    </div>
                    <div className="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" className="form-control" name='email' value={ credentials.email } onChange={ onChange } id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
                        <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input type="password" className="form-control" name='password' value={ credentials.password } onChange={ onChange } id="exampleInputPassword1" placeholder="Password" />
                    </div>
                    <div className="form-group">
                        <label for="exampleInputPassword1">Address</label>
                        <input type="Address" className="form-control" name='geolocation' value={ credentials.geolocation } onChange={ onChange } id="exampleInputAddress1" placeholder="Address" />
                    </div>
                    <button type="submit" className="m-3 btn btn-primary">Submit</button>
                    <Link to="/login" className="m-3 btn btn-danger">Already a user</Link>
                </form>
            </div>
        </>
    )
}
